import { useContext } from "react";
function AddToCart({ product, cart, incrementCart: incrementCartFunc, DecrementCart: DecrementCartFunc }) {

  function incrementCart() {
    incrementCartFunc(product);
  }
  function DecrementCart() {
    DecrementCartFunc(product);
  }


  const quantity = cart[product.id] ? cart[product.id].quantity : 0;
  if (quantity === 0) {
    return (
      <div>
        <button onClick={() => { incrementCart() }}>Add to cart</button>
      </div>
    )
  }
  return (
    <div>
      <button onClick={() => { incrementCart() }}>+</button>
      <span>{quantity}</span>
      <button onClick={() => { DecrementCart() }}>-</button>
    </div>
  )

}
export default AddToCart;

